mapper.update:
UPDATE cart
SET item_cnt = #{itemCnt} ,
    item_ck = #{itemCk}
WHERE cust_id = #{custId}
AND item_id = #{itemId}

UPDATE cart
SET item_cnt = item_cnt + #{itemCnt} ,
    item_ck = #{itemCk}
WHERE cust_id = #{custId}
  AND item_id = #{itemId}

UPDATE cart
SET item_ck = "N"
WHERE cust_id = #{custId}

mapper.delete:
DELETE FROM cart
WHERE item_id = #{itemId}
AND cust_id = #{custId}

DELETE FROM cart
WHERE cust_id = #{custId}
  AND item_ck = #{itemCk}
