mapper.select:
select  item_id, name, exp, price, dc_rt, item_dc_amt, rev_cnt, ship_type
from ITEM
where cate_code LIKE CONCAT(#{cateCode},'%')

    AND price-item_dc_amt < #{filter.price1}


    AND price-item_dc_amt >= #{filter.price1} AND price-item_dc_amt < #{filter.price2}


    AND price-item_dc_amt >= #{filter.price2} AND price-item_dc_amt < #{filter.price3}


    AND price-item_dc_amt >= #{filter.price3}


    ORDER BY price - item_dc_amt ASC


    ORDER BY price - item_dc_amt DESC


    ORDER BY sell_cnt DESC


    ORDER BY dc_rt DESC


    ORDER BY reg_dt DESC

LIMIT #{limit} OFFSET #{start}

select  count(*)
from ITEM
where cate_code LIKE CONCAT(#{cateCode},'%')


    AND price-item_dc_amt < #{filter.price1}


    AND price-item_dc_amt >= #{filter.price1} AND price-item_dc_amt < #{filter.price2}


    AND price-item_dc_amt >= #{filter.price2} AND price-item_dc_amt < #{filter.price3}


    AND price-item_dc_amt >= #{filter.price3}


    ORDER BY price - item_dc_amt ASC


    ORDER BY price - item_dc_amt DESC


    ORDER BY sell_cnt DESC


    ORDER BY dc_rt DESC


    ORDER BY reg_dt DESC

select item_id, name, exp, price, dc_rt, item_dc_amt, rev_cnt, ship_type
from ITEM
WHERE item_id IN

        #{item}

limit #{limit} offset #{start}

select item_id, name, exp, price, dc_rt, item_dc_amt, rev_cnt, ship_type from ITEM WHERE item_id IN
#{item}
ORDER BY price ASC LIMIT 1

select item_id, name, exp, price, dc_rt, item_dc_amt, rev_cnt, ship_type from ITEM WHERE item_id IN
#{item}
ORDER BY price DESC LIMIT 1

