mapper.select:
SELECT item_ann
FROM item_announcement
WHERE item_id = #{itemId}

mapper.update:
UPDATE search_keyword
SET item_id_list = #{itemIdList}
WHERE keyword = #{keyword}

mapper.select:
SELECT item_id_list
FROM search_keyword
WHERE keyword=#{keyword}

mapper.update:
UPDATE SEARCH_KEYWORD
SET item_id_list = CONCAT(item_id_list,',',#{itemIdList})
WHERE keyword = #{keyword}
